const navigation=document.querySelector(".navigation"),menu=document.querySelector(".navigation__menu"),hamburger=document.querySelector(".hamburger"),windowY=window.innerHeight,breakpoint=.2*windowY,handleMenu=()=>{hamburger.classList.toggle("hamburger-transform"),menu.classList.toggle("show-menu")};window.addEventListener("scroll",()=>{const a=window.scrollY;a>breakpoint?navigation.classList.add("fixed-navigation"):navigation.classList.remove("fixed-navigation")}),hamburger.addEventListener("click",()=>handleMenu()),menu.addEventListener("click",()=>handleMenu());